import requests
import json

# Suponiendo que ya tienes ioc_lists como lista de dicts
# Ejemplo: ioc_lists = [{"ioc_value": "8.8.8.8"}, {"ioc_value": "malicious.com"}]

MISP_URL = "$misp_url/attributes/restSearch"
MISP_KEY = "$misp_api_key"
HEADERS = {
    "Authorization": MISP_KEY,
    "Accept": "application/json",
    "Content-Type": "application/json"
}

ioc_lists = $extract_ioc.message.alert_iocs
if isinstance(ioc_lists, str):
  try:
    ioc_lists = json.loads(ioc_lists)
  except json.JSONDecodeError:
    print("Error: No se pudo parsear los IoCs como JSON")
    exit(1)

resultados = []

for ioc in ioc_lists:
    value = ioc.get("ioc_value")
    if not value:
        continue
    payload = {"value": value}
    try:
        response = requests.post(MISP_URL, headers=HEADERS, json=payload, verify=False)
        response.raise_for_status()
        resultados.append({
            "ioc_value": value,
            "result": response.json()
        })
    except Exception as e:
        resultados.append({
            "ioc_value": value,
            "error": str(e)
        })

# Filtrar IoCs con resultados encontrados
ioc_con_resultados = []
for item in resultados:
    atributos = item.get("result", {}).get("response", {}).get("Attribute", [])
    if atributos:

                for attr in atributos:
                    event = attr.get("Event", {})
                    ioc_con_resultados.append({
                        "ioc_value": item["ioc_value"],
                        "category": attr.get("category", ""),
                        "comment": attr.get("comment", ""),
                        "event_id": attr.get("event_id", ""),
                        "value": attr.get("value", ""),
                        "event_info": event.get("info", ""),
                        "event_uuid": event.get("uuid", ""),
                        "event_org_id": event.get("org_id", ""),
                        "event_orgc_id": event.get("orgc_id", ""),
                        "event_publish_timestamp": event.get("publish_timestamp", "")
                    })

# Imprimir solo los IoCs con resultados o 'false' si no hay ninguno
if ioc_con_resultados:
    print(json.dumps(ioc_con_resultados, indent=2, ensure_ascii=False))
else:
    print('false')

